import './App.css';
import { useState } from 'react';

function App() {
  const [counter, setCounter] = useState(0);

  const counterArray = [];

  function add() {
    setCounter(counter + 1)
  }

  function remove() {
    setCounter(counter - 1)
  }


  return (
    <div className="App">
      <div>
        <h1>Compteur</h1>

        <div>{counter}</div>

        <button onClick={add}>+</button>
        <button onClick={remove}>-</button>
      </div>
    </div>
  );
}

export default App;
