import './App.css';
import { useState } from 'react';

function add() {
  setCounter(counter + 1)
}

function remove() {
  setCounter(counter - 1)
}

function Counter({counter, add, remove}) {
  return (
    <div>
      <h1>Compteur</h1>

      <div>{counter}</div>

      <button onClick={() => {add()}}>+</button>
      <button onClick={() => {remove()}}>-</button>
    </div>
  )
}

function App() {
  const [counter, setCounter] = useState(0);


  return (
    <div className="App">
      <Counter counter={counter} />
    </div>
  );
}

export default App;
