import './App.css';
import { useState } from 'react';

function Counter(props) {
  const counter = props.counter
  return (
    <div>
      <h1>Compteur</h1>

      <div>{counter}</div>

      <button>+</button>
      <button>-</button>
    </div>
  )
}

function App() {
  const [counter, setCounter] = useState(0);

  const counterArray = [];

  function add() {
    setCounter(counter + 1)
  }

  function remove() {
    setCounter(counter - 1)
  }

  return (
    <div className="App">
      <Counter counter={counter} />
    </div>
  );
}

export default App;
